import React, { useState } from 'react';

import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import './Expenses.css';
import ExpensesFilter from './NewExpense/ExpensesFilter';

function Expenses(props) {

    const [dropDownValue, setDropDownValue] = useState('2019');


    const DropDownChangeHandler = (dropValue) => {
        console.log(dropValue);
        setDropDownValue(dropValue);
    }

    return <div>
    <Card className="expenses">
    <ExpensesFilter defaultValue={dropDownValue} onChangeValue={DropDownChangeHandler}/>
    {props.item.map(expense => <ExpenseItem title={props.title} date={props.date} amount={props.amount}/>)}
    <ExpenseItem 
    title={props.item[0].title} 
    date={props.item[0].date} 
    amount={props.item[0].amount}>
    </ExpenseItem>

    <ExpenseItem 
    title ={props.item[1].title} 
    date={props.item[1].date} 
    amount={props.item[1].amount}>
    </ExpenseItem>

    <ExpenseItem 
    title ={props.item[2].title} 
    date={props.item[2].date} 
    amount={props.item[2].amount}>
    </ExpenseItem>
    </Card>
    </div>
}

export default Expenses;
